import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios, * as others from "axios";

function Shopper() {
  const [shopperList, setShopperList] = useState([]);
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(
          "http://localhost:3000/api/getAllShoppers"
        );
        console.log(response);
        setShopperList(response.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetchData();
  }, []);

  const navigate = useNavigate();
  function manageClickPreview() {
    navigate("/PreviewList");
  }
  function manageClickCreated() {
    navigate("/CreateList");
  }

  return (
    <div className="list-item">
      <div>
      const shopperList = Shopper.map(Shopper =>
  <li>
     <img
       
       alt={person.name}
     />
     <p>
       <b>{person.name}:</b>
       {' ' + person.profession + ' '}
       known for {person.accomplishment}
     </p>
  </li>
);

      </div>
      <div>
        <button onClick={(e) => manageClickCreated()}>
          Make a shopping list
        </button>
        <button onClick={(e) => manageClickPreview()}>
          Preview shopping list
        </button>
      </div>
    </div>
  );
}

export default Shopper;
